
public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//char ch='a';
		String name="Jan";
		switch(name)
		{
		case "Jan":System.out.println("beginning of the year");
		break;
		case "Feb":System.out.println("Second month");
		break;
		default:
			System.out.println("end of the year");
		}

	}

}
